import { defaultCountries, parseCountry, buildCountryData } from 'react-international-phone';

// To see which iso2 maps to which country, see
// https://github.com/goveo/react-international-phone/blob/master/src/data/countryData.ts
const getCustomCountries = () => defaultCountries
    .map(parseCountry)
    .filter(({ iso2 }) => supportedCountries.includes(iso2))
    .map((country) => {
    if (country.iso2 === 'br')
        country.format = '(..) .....-....';
    if (country.iso2 === 'fr')
        country.format = '(.) .. .. .. ..';
    return buildCountryData(country);
});
/** Which countries to show in phone number input country dropdowns */
const supportedCountries = ['us', 'ca'];

export { getCustomCountries, supportedCountries };
